import React from 'react';
import {View, Text, ImageBackground, StyleSheet, Image} from 'react-native';
import creditcardutils from 'creditcardutils';
import {widthPercentageToDP} from '../Utils/DpToPixel';

const Purchases = props => {
  const {number, expiration, name} = props;
  const formatedNumber = creditcardutils.formatCardNumber(String(number));
  let cardTypeIcon = null;
  switch (creditcardutils.parseCardType(number.toString())) {
    case 'amex':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
    case 'dinersclub':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
    case 'discover':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
    case 'jcb':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
    case 'maestro':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
    case 'mastercard':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
    case 'unionpay':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
    case 'visa':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
    case 'visaelectron':
      cardTypeIcon = {
        uri: 'data:image/png;base64,',
      };
      break;
  }

  return (
    <View style={styles.container}>
      <ImageBackground
        source={require('../../assets/creditCard.png')}
        style={styles.creditCard}
        imageStyle={styles.border}>
        <View style={styles.imageContainer}>
          {cardTypeIcon && (
            <View style={styles.cardTypeIconContainer}>
              <Image
                source={{uri: cardTypeIcon?.uri}}
                style={styles.cardType}
                resizeMode="contain"
              />
            </View>
          )}
          <Text style={styles.number}>
            {'**** **** **** ' + formatedNumber.substr(-4)}
          </Text>
          <View style={styles.row}>
            <View>
              <Text style={styles.label}>CARDHOLDER NAME</Text>
              <Text style={styles.text}>{name}</Text>
            </View>
            <View>
              <Text style={styles.label}>VALID</Text>
              <Text style={styles.text}>
                {creditcardutils.formatCardExpiry(expiration.toString())}
              </Text>
            </View>
          </View>
        </View>
      </ImageBackground>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    elevation: 4,
  },
  creditCard: {
    width: widthPercentageToDP('92%'),
    height: 220,
    alignSelf: 'center',
  },
  imageContainer: {
    paddingHorizontal: 13,
    height: 210,
    width: widthPercentageToDP('92%'),
  },
  border: {
    borderRadius: 20,
  },
  cardType: {
    height: 32,
    width: 32 * (132 / 80),
  },
  cardTypeIconContainer: {
    justifyContent: 'center',
    height: 27,
    width: 27 * (125 / 80),
    alignItems: 'center',
    alignSelf: 'flex-end',
    margin: 10,
  },
  number: {
    color: '#f6f6f6',
    fontFamily: 'Roboto-Light',
    fontSize: 33,
    textAlign: 'center',
    top: 25,
  },
  row: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    top: 60,
  },
  label: {
    color: '#dbdbdb',
    fontFamily: 'Roboto-Light',
    fontSize: 15,
  },
  text: {
    fontFamily: 'Roboto-Medium',
    fontSize: 15,
    color: '#dbdbdb',
  },
});

export default Purchases;
